import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from '@material-ui/core/TextField';
import { Grid } from '@material-ui/core';
import allowedMovies from './data/allowed_movies.json';
import useStyles from './styles';

const Search = () => {
  const classes = useStyles();
  const [searchTerm, setSearchTerm] = useState('');
  const [options, setOptions] = useState([]);

  useEffect(() => {
    const fetchMovieTitles = async () => {
        try {
          const response = await axios.get('https://api.themoviedb.org/3/search/movie', {
            params: {
              api_key: '8baba8ab6b8bbe247645bcae7df63d0d',
              query: searchTerm
            }
          });
          const firstMatch = response.data.results.find(movie => allowedMovies.movies.includes(movie.title));
          setOptions(firstMatch ? [firstMatch.title] : []);
        } catch (error) {
          console.error(error);
        }
      };      
    fetchMovieTitles();
  }, [searchTerm]);

  return (
    <Grid container justify="center" alignItems="center" style={{ height: '100vh' }}>
      <Grid item xs={12} sm={6} md={4}>
        <Autocomplete
          freeSolo
          options={options}
          renderInput={params => (
            <TextField
              {...params}
              className={classes.searchInput}
              label="Search movie titles"
              variant="outlined"
              value={searchTerm}
              onChange={event => setSearchTerm(event.target.value)}
            />
          )}
        />
      </Grid>
    </Grid>
  );
};

export default Search;
