import React, { useState } from 'react';
import './ImageDisplay.css';

const ImageDisplay = () => {
  const [selectedButton, setSelectedButton] = useState(1);

  const handleButtonClick = () => {
    if (selectedButton < 3) {
      setSelectedButton(selectedButton + 1);
    }
  };

  return (
    <div className="image-container">
      <div className="image-wrapper">
        {selectedButton === 1 && (
          <img
            src="https://images.freeimages.com/images/previews/ac9/railway-hdr-1361893.jpg"
            alt="example 1"
            className="image"
          />
        )}
        {selectedButton === 2 && (
          <img
            src="https://images.pexels.com/photos/268533/pexels-photo-268533.jpeg?cs=srgb&dl=pexels-pixabay-268533.jpg&fm=jpg"
            alt="example 2"
            className="image"
          />
        )}
        {selectedButton === 3 && (
          <img
            src="https://thumbs.dreamstime.com/b/beautiful-rain-forest-ang-ka-nature-trail-doi-inthanon-national-park-thailand-36703721.jpg"
            alt="example 3"
            className="image"
          />
        )}
      </div>
      <div className="buttons">
        <button
          className={`image-button ${selectedButton === 1 ? 'selected' : ''}`}
          onClick={() => setSelectedButton(1)}
        >
          1
        </button>
        {selectedButton > 1 && (
          <button
            className={`image-button ${selectedButton === 2 ? 'selected' : ''}`}
            onClick={() => setSelectedButton(2)}
          >
            2
          </button>
        )}
        {selectedButton > 2 && (
          <button
            className={`image-button ${selectedButton === 3 ? 'selected' : ''}`}
            onClick={() => setSelectedButton(3)}
          >
            3
          </button>
        )}

      </div>
    </div>
  );
};

export default ImageDisplay;
