import React, { useState } from 'react';
import './ImageDisplay.css';

const Image = ({ imageURL }) => {
  return (
    <div className="image-container">
      <div className="image-wrapper">
        <img src={imageURL} alt="example" className="image" />
      </div>
    </div>
  );
};

const ImageDisplay = ({ imageNumber, imageURL, setImageURL }) => {
  const [selectedImage, setSelectedImage] = useState(imageNumber);

  const handleImageChange = (imageNumber, newImageURL) => {
    setSelectedImage(imageNumber);
    setImageURL(newImageURL);
  };

  return (
    <div>
      <button
        key={imageNumber}
        className="image-button"
        onClick={() => handleImageChange(imageNumber, 'new-image-url')}
      >
        {imageNumber}
      </button>
      {selectedImage === imageNumber && <Image imageURL={imageURL} />}
    </div>
  );
};

export default ImageDisplay;
