import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from '@material-ui/core/TextField';
import Chip from '@material-ui/core/Chip';
import { makeStyles } from '@material-ui/core/styles';
import allowedMovies from './data/allowed_movies.json';

const useStyles = makeStyles(theme => ({
  searchInput: {
    width: '100%',
    maxWidth: 400
  }
}));

const Search = () => {
  const classes = useStyles();
  const [searchTerm, setSearchTerm] = useState('');
  const [options, setOptions] = useState([]);

  useEffect(() => {
    const fetchMovieTitles = async () => {
        try {
          const response = await axios.get('https://api.themoviedb.org/3/search/movie', {
            params: {
              api_key: '8baba8ab6b8bbe247645bcae7df63d0d',
              query: searchTerm
            }
          });
          const movieTitles = response.data.results.map(movie => movie.title);
          const filteredTitles = movieTitles.filter(title => allowedMovies.movies.includes(title));
          setOptions(filteredTitles);
        } catch (error) {
          console.error(error);
        }
      };
    fetchMovieTitles();
  }, [searchTerm]);

  return (
    <Autocomplete
      freeSolo
      options={options}
      renderInput={params => (
        <TextField
          {...params}
          className={classes.searchInput}
          label="Search movie titles"
          variant="outlined"
          value={searchTerm}
          onChange={event => setSearchTerm(event.target.value)}
        />
      )}
    />
  );
};

export default Search;
