import React, { useState } from 'react';

const ImageDisplay = () => {
  const [selectedImage, setSelectedImage] = useState(1);

  const handleImageChange = (imageNumber) => {
    setSelectedImage(imageNumber);
  };

  return (
    <div className="image-container">
      {selectedImage === 1 && (
        <img
          src="https://images.freeimages.com/images/previews/ac9/railway-hdr-1361893.jpg"
          alt="example 1"
          className="image"
        />
      )}
      {selectedImage === 2 && (
        <img
          src="https://images.freeimages.com/images/previews/724/nature-waterfall-1399329.jpg"
          alt="example 2"
          className="image"
        />
      )}
      {selectedImage === 3 && (
        <img
          src="https://images.freeimages.com/images/previews/41b/london-night-2-1364286.jpg"
          alt="example 3"
          className="image"
        />
      )}
      <div className="buttons">
        <button onClick={() => handleImageChange(1)}>1</button>
        <button onClick={() => handleImageChange(2)}>2</button>
        <button onClick={() => handleImageChange(3)}>3</button>
      </div>
    </div>
  );
};

export default ImageDisplay;
