import React, { useState } from 'react';
import './ImageDisplay.css';

const ImageDisplay = ({ selectedImage, numButtons }) => {
  const [numDisplayedButtons, setNumDisplayedButtons] = useState(1);

  const handleButtonClick = (buttonNumber) => {
    setSelectedImage(buttonNumber);
  };

  const renderButtons = () => {
    const buttons = [];
    for (let i = 1; i <= numButtons; i++) {
      buttons.push(
        <button
          key={i}
          className={`image-button ${selectedImage === i ? 'selected' : ''}`}
          onClick={() => handleButtonClick(i)}
        >
          {i}
        </button>
      );
    }
    return buttons;
  };

  return (
    <div className="image-container">
      <div className="image-wrapper">
        {selectedImage === 1 && (
          <img
            src="https://images.freeimages.com/images/previews/ac9/railway-hdr-1361893.jpg"
            alt="example 1"
            className="image"
          />
        )}
        {selectedImage === 2 && (
          <img
            src="https://images.pexels.com/photos/268533/pexels-photo-268533.jpeg?cs=srgb&dl=pexels-pixabay-268533.jpg&fm=jpg"
            alt="example 2"
            className="image"
          />
        )}
        {selectedImage === 3 && (
          <img
            src="https://thumbs.dreamstime.com/b/beautiful-rain-forest-ang-ka-nature-trail-doi-inthanon-national-park-thailand-36703721.jpg"
            alt="example 3"
            className="image"
          />
        )}
      </div>
      <div className="buttons">
        {renderButtons()}
        {numDisplayedButtons < numButtons && (
          <button
            className="add-button"
            onClick={() => setNumDisplayedButtons(numDisplayedButtons + 1)}
          >
            +
          </button>
        )}
      </div>
    </div>
  );
};

export default ImageDisplay;
