import React, { useState } from 'react';
import './ImageDisplay.css';



const ImageDisplay = ({ imageNumber, imageURL }) => {
  const [selectedImage, setSelectedImage] = useState(imageNumber);

  const handleImageChange = (imageNumber) => {
    setSelectedImage(imageNumber);
  };

  return (
    <div className="image-container">
      <div className="image-wrapper">
        {selectedImage === imageNumber && (
          <img
            src={imageURL}
            alt={`example ${imageNumber}`}
            className="image"
          />
        )}
      </div>
      <div className="buttons">
        <button
          className={`image-button ${selectedImage === imageNumber ? 'selected' : ''}`}
          onClick={() => handleImageChange(imageNumber)}
        >
          {imageNumber}
        </button>
      </div>
    </div>
  );
};

export default ImageDisplay;
