import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from '@material-ui/core/TextField';
import allowedMovies from './data/allowed_movies.json';
import ImageDisplay from '../ImageDisplay/ImageDisplay.jsx';
import './SearchBar.css';

const Search = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [options, setOptions] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedButton, setSelectedButton] = useState(null);

  useEffect(() => {
    const fetchMovieTitles = async () => {
      try {
        const response = await axios.get('https://api.themoviedb.org/3/search/movie', {
          params: {
            api_key: '8baba8ab6b8bbe247645bcae7df63d0d',
            query: searchTerm
          }
        });
        const matches = response.data.results.filter(movie => allowedMovies.movies.includes(movie.title));
        const titles = matches.map(movie => movie.title);
        setOptions(titles);
      } catch (error) {
        console.error(error);
      }
    };      
    fetchMovieTitles();
  }, [searchTerm]);

  const handleButtonSearch = (imageNumber) => {
    setSelectedImage(imageNumber);
  };

  const handleButtonClick = (buttonNumber) => {
    setSelectedButton(buttonNumber);
  };

  return (
    <form className="search-container">
      <Autocomplete
        freeSolo
        options={options}
        renderInput={params => (
          <TextField
            {...params}
            className="search-input"
            label="Search movie titles"
            variant="outlined"
            value={searchTerm}
            onChange={event => setSearchTerm(event.target.value)}
            InputProps={{
              ...params.InputProps,
              className: 'search-input',
            }}
          />
        )}
        renderOption={option => <div className="search-option">{option}</div>}
        getOptionLabel={option => option}
        popupIcon={<div className="search-indicator">&#x25BC;</div>}
      />
      <button className={`image-button ${selectedButton === 1 ? 'selected' : ''}`} onClick={() => handleButtonClick(1)}>1</button>
      <button className={`image-button ${selectedButton === 2 ? 'selected' : ''}`} onClick={() => handleButtonClick(2)}>2</button>
      <button className={`image-button ${selectedButton === 3 ? 'selected' : ''}`} onClick={() => handleButtonClick(3)}>3</button>
      <button className="search-button" type="submit" onClick={() => handleButtonSearch(selectedButton)}>
        Submit
      </button>
      {selectedImage && <ImageDisplay selectedImage={selectedImage} />}
    </form>
  );
};

export default Search;
